package com.geeks.emil_maldybaev_hw4;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentContainer;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.geeks.emil_maldybaev_hw4.databinding.FragmentContinentBinding;

import java.util.ArrayList;

public class ContinentFragment extends Fragment implements OnClick{
    private FragmentContinentBinding binding;
    private ArrayList <Continent> listContinent;
    private ContinentAdapter adapter;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentContinentBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadData();
        adapter = new ContinentAdapter(listContinent, this::onClick);
        binding.rvContinent.setAdapter(adapter);
    }

    public void onClick(int position) {
        Bundle bundle = new Bundle();
        bundle.putInt("key", position);
        CountryFragment countryFragment = new CountryFragment();
        countryFragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().beginTransaction().replace(R.id.container, countryFragment).addToBackStack(null).commit();
    }

    private void loadData() {
        listContinent = new ArrayList<>();
        listContinent.add(new Continent("Евразия", "https://thumbs.dreamstime.com/z/%D0%B5%D0%B2%D1%80%D0%B0%D0%B7%D0%B8%D1%8F-%D0%BA%D0%BE%D0%BD%D1%82%D0%B8%D0%BD%D0%B5%D0%BD%D1%82-%D1%81-%D0%BA%D0%BE%D0%BD%D1%82%D1%83%D1%80%D0%B0%D0%BC%D0%B8-%D1%81%D1%82%D1%80%D0%B0%D0%BD-%D0%B2%D0%B5%D0%BA%D1%82%D0%BE%D1%80%D0%BD%D1%8B%D0%B9-%D1%80%D0%B8%D1%81%D1%83%D0%BD%D0%BE%D0%BA-%D0%BE%D1%81%D1%82%D1%80%D0%BE%D0%B2%D0%B0-194807770.jpg"));
        listContinent.add(new Continent("Африка", "data:image/jpeg;base64,"));
        listContinent.add(new Continent("Южная Америка", "data:image/jpeg;base64,"));
        listContinent.add(new Continent("Северная Америка", "https://upload.wikimedia.org/wikipedia/commons/thumb/9/99/North_America_satellite_globe.jpg/220px-North_America_satellite_globe.jpg"));
        listContinent.add(new Continent("Австралия", "https://upload.wikimedia.org/wikipedia/commons/thumb/d/da/Australia_with_AAT_%28orthographic_projection%29.svg/300px-Australia_with_AAT_%28orthographic_projection%29.svg.png"));
    }

}